import React from 'react'
import 'semantic-ui-css/semantic.min.css'
import { Image } from 'semantic-ui-react'
import mmImg from "../img/metamask.png";
import { Button, Modal } from 'semantic-ui-react'

class Poetry extends React.Component {

    state={
        hasMM: false,
        open:false,
        btnPositive: true,
        btnText: "Connect MetaMask",
        btnDisabled: false
    }

    componentDidMount(){
        if (typeof window.ethereum !== 'undefined') {
            console.log('MetaMask is installed!');
            this.setState({hasMM: true})
            if (window.ethereum.isConnected()){
                console.log("ethereum is connected" + window.ethereum.isConnected())
                this.setState({btnText:"MetaMask Connected"})
                this.setState({btnPositive:false})
                this.setState({btnDisabled:true})
            }
          }

    }
   async checkMMInstall(event){
        event.preventDefault()
        console.log(this.state.hasMM)
        if(this.state.hasMM === false){
            this.setState({open: true})
        } else {
          await  window.ethereum.request({ method: 'eth_requestAccounts' });
          this.setState({btnText:"MetaMask Connected"})
          this.setState({btnPositive:false})
          this.setState({btnDisabled:true})
        }

    }

    render(){
        return(
            <div>
                {/* image from "https://cdn.iconscout.com/icon/free/png-512/metamask-2728406-2261817.png" */}
                <Image src={mmImg} size='small' circular />

                <Modal
      onClose={() => this.setState({open:false})}
      open={this.state.open}
      trigger={<Button positive={this.state.btnPositive} disabled={this.state.btnDisabled} onClick={this.checkMMInstall.bind(this)}>{this.state.btnText}</Button>}
    >
 <Modal.Header>Error</Modal.Header>
      <Modal.Content>
    Your don't have MetaMask Extension Installed
      </Modal.Content>
      <Modal.Actions>
        <Button
          content="OK"
          labelPosition='right'
          icon='checkmark'
          onClick={() => this.setState({open:false})}
          positive
        />
      </Modal.Actions>
    </Modal>

            </div>
        )
    }
}
export default Poetry;
