import React from "react"
import { Dropdown, Input  } from 'semantic-ui-react'
import "../App.css"



const options = [
    { key: 1, text: 'ETH', value: 1 },
    { key: 2, text: 'UNI', value: 2 },
    { key: 3, text: 'DAI', value: 3 },
  ]

const euPool=1200
const edPool=1200
const udPool=1200

let ethNum=20
let uniNum=60
let daiNum=200

let ethPrice=30
let uniPrice=10
let daiPrice=20




class Otoken extends React.Component {
    state={
        tokenNum: null
    }

    componentDidMount(){
        console.log(options)
    }

    async handleChangeNum1(event){
        if(this.state.value !== null){
            console.log(event)
            await this.setState({tokenNum: event.target.value})     
        }


    }
    async handleChangeToken1(event,{value}){
        await this.setState({value})
    }
    render(){
        const { value } = this.state
        return(
            <div>
              <Input
              className="token-input"
              onChange={this.handleChangeNum1.bind(this)}
    label={<Dropdown defaultValue='ETH' options={options} onChange={this.handleChangeToken1.bind(this)} value={value}/>}
    labelPosition='left'
    placeholder='0.00'
  />

           <Input
           disabled
    label={<Dropdown defaultValue='ETH' options={options} />}
    labelPosition='left'
    placeholder='0.00'
  />
            </div>
        )
    }
}

export default Otoken;
