import React from 'react'
import 'semantic-ui-css/semantic.min.css'
import { Image } from 'semantic-ui-react'
import mmImg from "../img/metamask.png";
import { Button, Modal } from 'semantic-ui-react'

class Poetry extends React.Component {

    state={
        hasMM: false
    }

    componentDidMount(){
        if (typeof window.ethereum !== 'undefined') {
            console.log('MetaMask is installed!');
            this.setState({hasMM: true})
          }
    }
    checkMMInstall(){
        if (!this.state.hasMM){
return (
    <Modal
      open={this.state.hasMM}
      header='Reminder!'
      content="You haven't install Meta Mask extension yet"
      actions={['Snooze', { key: 'done', content: 'Done', positive: true }]}
    />
)
        } else {
            return (
                <Modal
                open={this.state.hasMM}
                  header='Reminder!'
                  content="You haven't install Meta Mask extension yet"
                  actions={['Snooze', { key: 'done', content: 'Done', positive: true }]}
                />
            )
        }
    }

    render(){
        return(
            <div>
                {/* image from "https://cdn.iconscout.com/icon/free/png-512/metamask-2728406-2261817.png" */}
                <Image src={mmImg} size='small' circular />
                <Button positive onClick={this.checkMMInstall.bind(this)}>Connect MetaMask</Button>
            </div>
        )
    }
}
export default Poetry;
