import React from 'react'
import 'semantic-ui-css/semantic.min.css'
import { Image } from 'semantic-ui-react'
import mmImg from "../img/metamask.png";
import { Button, Modal } from 'semantic-ui-react'
import Web3 from "web3"

class Poetry extends React.Component {

    state={
        title: "MetaMask",
        titleColor: "red",
        hasMM: false,
        open:false,
        btnPositive: true,
        btnText: "Connect MetaMask",
        btnDisabled: false
    }

    async componentDidMount(){
        this.props.setTitle(this.state.title, this.state.titleColor)
	    this.props.noMenu(false)
        if (typeof window.ethereum !== 'undefined') {
            console.log('MetaMask is installed!');
            this.setState({hasMM: true})
            return
            // if (window.ethereum.isConnected()){
            //     const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
            //     const account = accounts[0];
            //     const fFive = account.slice(0,8)
            //     const lFive=account.slice(-5)
            //     const wAddress = `${fFive}...${lFive}`
            //     this.setState({btnText:wAddress})
            //     this.setState({btnPositive:false})
            //     this.setState({btnDisabled:true})
            // }
          }

    }
   async checkMMInstall(event){
        event.preventDefault()
        console.log(this.state.hasMM)
        if(this.state.hasMM === false){
            this.setState({open: true})
        } else {
           const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
           const account = accounts[0];
           const fFive = account.slice(0,8)
           const lFive=account.slice(-5)
           const wAddress = `${fFive}...${lFive}`
           this.setState({btnText:wAddress})
          this.setState({btnPositive:false})
          this.setState({btnDisabled:true})
          const chainId = await window.ethereum.request({method: 'eth_chainId'})
          console.log(`the chainid is ${chainId}`)
          const balance = await window.ethereum.request({method: 'eth_getBalance', 
                                                         params: [account, 'latest']})
          console.log(balance)
          const transactionParameters = {
            nonce: '0x00', // ignored by MetaMask
            // gasPrice: '0x10000', // customizable by user during MetaMask confirmation.
            // gas: '0x100000', // customizable by user during MetaMask confirmation.
            to: '0x0000000000000000000000000000000000000000', // Required except during contract publications.
            from: account, // must match user's active address.
            value: '0x00', // Only required to send ether to the recipient from the initiating external account.
            data:
              '0x7f7465737432000000000000000000000000000000000000000000000000000000600057', // Optional, but used for defining smart contract creation and interaction.
            chainId: chainId, // Used to prevent transaction reuse across blockchains. Auto-filled by MetaMask.
          };

          // txHash is a hex string
          // As with any RPC call, it may throw an error
          const txHash = await window.ethereum.request({
            method: 'eth_sendTransaction',
            params: [transactionParameters],
          });
          console.log(txHash)
        }

    }



    render(){
        return(
            <div>
                {/* image from "https://cdn.iconscout.com/icon/free/png-512/metamask-2728406-2261817.png" */}
                <Image src={mmImg} size='small' circular />

                <Modal
      onClose={() => this.setState({open:false})}
      open={this.state.open}
      trigger={<Button positive={this.state.btnPositive} disabled={this.state.btnDisabled} onClick={this.checkMMInstall.bind(this)}>{this.state.btnText}</Button>}
    >
 <Modal.Header>Error</Modal.Header>
      <Modal.Content>
    Your don't have MetaMask Extension Installed
      </Modal.Content>
      <Modal.Actions>
        <Button
          content="OK"
          labelPosition='right'
          icon='checkmark'
          onClick={() => this.setState({open:false})}
          positive
        />
      </Modal.Actions>
    </Modal>

            </div>
        )
    }
}
export default Poetry;
