import React from 'react'
import 'semantic-ui-css/semantic.min.css'
import { Image } from 'semantic-ui-react'
import mmImg from "../img/metamask.png";
import { Button, Modal } from 'semantic-ui-react'

class Poetry extends React.Component {

    state={
        hasMM: false,
        open:false
    }

    componentDidMount(){
        if (typeof window.ethereum !== 'undefined') {
            console.log('MetaMask is installed!');
            this.setState({hasMM: true})
          }
    }
    checkMMInstall(event){
        event.preventDefault()
        if(!this.state.hasMM){
            this.setState({open: true})
        }

    }

    render(){
        return(
            <div>
                {/* image from "https://cdn.iconscout.com/icon/free/png-512/metamask-2728406-2261817.png" */}
                <Image src={mmImg} size='small' circular />

                <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button positive onClick={this.checkMMInstall.bind(this)}>Connect MetaMask</Button>}
    >
      <Modal.Header>Select a Photo</Modal.Header>
      <Modal.Content image>
        <Image size='medium' src='https://react.semantic-ui.com/images/avatar/large/rachel.png' wrapped />
        <Modal.Description>
          <Header>Default Profile Image</Header>
          <p>
            We've found the following gravatar image associated with your e-mail
            address.
          </p>
          <p>Is it okay to use this photo?</p>
        </Modal.Description>
      </Modal.Content>
      <Modal.Actions>
        <Button color='black' onClick={() => setOpen(false)}>
          Nope
        </Button>
        <Button
          content="Yep, that's me"
          labelPosition='right'
          icon='checkmark'
          onClick={() => setOpen(false)}
          positive
        />
      </Modal.Actions>
    </Modal>
  )
            </div>
        )
    }
}
export default Poetry;
