    { key: 2, text: 'UNI', value: 2 },
    { key: 3, text: 'DAI', value: 3 },
  ]

class Otoken extends React.Component {
    state={}

    componentDidMount(){
        console.log(options)
    }

    async handleChangeNum1(event){
        console.log(event)
       await console.log(event.target.value)
    }
    async handleChangeToken1(event){
        console.log("========")
        console.log(event.target.name)
        // this.setState({topToken: })
    }
    render(){
        return(
            <div>
              <Input
              className="token-input"
              onChange={this.handleChangeNum1.bind(this)}
    label={<Dropdown defaultValue='ETH' options={options} onChange={this.handleChangeToken1.bind(this)}/>}
    labelPosition='left'
    placeholder='0.00'
  />

           <Input
           disabled
    label={<Dropdown defaultValue='ETH' options={options} />}
    labelPosition='left'
    placeholder='0.00'
  />
            </div>
        )
    }
}

export default Otoken;
